export default class Dom{static clear(e=document.body){e.innerHTML=""}static destroy(e=document.body){e.remove()}static insert(e,t=document.body,d="beforeend"){t.insertAdjacentHTML(d,e)}static replace(e,t){t.innerHTML=e}static append(e,t=document.body){t.appendChild(e)}static remove(e,t=document.body){t.removeChild(e)}}
