export default class Finder{static id(e){return document.getElementById(e)}static tag(e,t=document.body){return t.getElementsByTagName(e)}static query(e,t=document.body){return t.querySelector(e)}static queryAll(e,t=document.body){return t.querySelectorAll(e)}static queryLast(e,t=document.body){let r=Finder.queryAll(e,t);return r[r.length-1]}}
