export default class Cookie{static set(t,e=null,i=null,l=null){let o=null;if(i){let t=new Date;t.setTime(t.getTime()+24*i*60*60*1e3),o="; expires="+t.toUTCString()}document.cookie=`${t}=${e||""}${o||""}; path=${l||"/"}`}static get(t){let e=document.cookie.split(";").filter((e=>e.trim().startsWith(`${t}=`)));if(e.length>0)return e[0].trim().split("=")[1]}static has(t){return document.cookie.includes(t)}static remove(t){document.cookie=`${t}=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;`}}
