export default class Query{static change(e,a){let t=window.location.search;return new URLSearchParams(t).set(e,a),t}static add(e,a){let t=window.location.search;return new URLSearchParams(t).append(e,a),t}static get(e){let a=window.location.search;return new URLSearchParams(a).get(e)}static remove(e){let a=window.location.search;return new URLSearchParams(a).delete(e),a}}
